// Combine user profile and statistics into a dashboard format
const user = $('Get Current User').first().json;
const stats = $('Get User Stats').first().json;

// Calculate account age in days
const accountAge = Math.floor((Date.now() - new Date(user.createdAt).getTime()) / (1000 * 60 * 60 * 24));

// Determine user activity level
let activityLevel = 'New User';
if (stats.totalBookmarks > 100) {
  activityLevel = 'Active User';
}
if (stats.totalBookmarks > 500) {
  activityLevel = 'Power User';
}
if (stats.totalBookmarks > 1000) {
  activityLevel = 'Super User';
}

// Create dashboard data
const dashboard = {
  user: {
    name: user.name || 'User',
    email: user.email,
    role: user.role,
    accountAge: accountAge,
    activityLevel: activityLevel,
    memberSince: user.createdAt
  },
  statistics: {
    totalBookmarks: stats.totalBookmarks,
    totalLists: stats.totalLists,
    totalTags: stats.totalTags,
    totalHighlights: stats.totalHighlights,
    totalAssets: stats.totalAssets
  },
  insights: {
    averageBookmarksPerList: stats.totalLists > 0 ? Math.round(stats.totalBookmarks / stats.totalLists) : 0,
    averageTagsPerBookmark: stats.totalBookmarks > 0 ? Math.round(stats.totalTags / stats.totalBookmarks * 100) / 100 : 0,
    highlightRatio: stats.totalBookmarks > 0 ? Math.round(stats.totalHighlights / stats.totalBookmarks * 100) / 100 : 0,
    assetRatio: stats.totalBookmarks > 0 ? Math.round(stats.totalAssets / stats.totalBookmarks * 100) / 100 : 0
  },
  summary: {
    totalItems: stats.totalBookmarks + stats.totalLists + stats.totalTags + stats.totalHighlights + stats.totalAssets,
    organizationScore: Math.min(100, Math.round((stats.totalLists * 10 + stats.totalTags * 2 + stats.totalHighlights * 1) / Math.max(1, stats.totalBookmarks) * 10))
  }
};

return [{ json: dashboard }];

